select activity_id, t2.div_name as activity_owner, activity_name, activity_start_date, activity_end_date, activity_cost, activity_description, activity_create_time, activity_create_by, activity_edit_time, activity_edit_by from tbl_activity t1 join tbl_user t2 on t1.activity_owner = t2.user_id
t1.activity_name like '%' #{name} '%'
and t2.div_name like '%' #{owner} '%'
and t1.activity_start_date > #{startDate}
and t1.activity_end_date < #{endDate}
order by t1.activity_create_time desc limit #{pageNo},#{pageSize}
